    private void btnSearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSearchActionPerformed
       String Name;
       int Num;
       Num = Integer.parseInt(textNumber.getText());
       Name = textName.getText();
       Library lib = new Library();
       ResultSet result;
       try{
       result = lib.searching(Name, Num);
       if (result  == null){

         jLabel8.setText("empty set");
       }
       else{
           this.dispose();
        searchOutput obj = new searchOutput( result );
       obj.setVisible(true);
       }
         
       }
       catch(Exception e){
           System.out.println(e);
       }
       
    }//GEN-LAST:event_btnSearchActionPerformed

